pragma solidity 0.8.15;

import {IResolver} from "../interfaces/IResolver.sol";
import {IConnector} from "../interfaces/IConnector.sol";

contract SendOutboundRootResolver is IResolver {
  IConnector public immutable CONNECTOR;

  constructor(address _connector) {
    CONNECTOR = IConnector(_connector);
  }

  function checker() external view override returns (bool canExec, bytes memory execPayload) {
    bytes32 outboundRoot = CONNECTOR.outboundRoot();
    execPayload = abi.encodeWithSelector(IConnector.sendMessage.selector, abi.encode(outboundRoot));
    return (true, execPayload);
  }
}
